(function(g,moduleId,obj){
	var pcs = "AIGenData";
	console.log(moduleId);
	var p = g.getProcessus(pcs, null);
	p.instantiate();
	var m = p.activate(); // start activities
	if (m.isOk())
	{
		// Select automation
		var actModule = m.get("Activity");
		m = p.lock(actModule.getActivity(), actModule.getAID());
		actModule.addDataFile("Field", "row_id", moduleId);
		var o = g.getProcessObject("Module");
		m = p.validate(actModule, o);
		var next = m.get("Activity");
		p.lock(next.getActivity(), next.getAID());
	}
	return obj.sendRedirect(HTMLTool.getProcessStartURL(pcs));


})([GRANT],[ROWID],[OBJECT]);

