importPackage(Packages.com.simplicite.util.engine);
function callAITools(prompt,spe,object) {
	var g = Grant.getSystemAdmin();
	// Class dynamique dans le module Demo
	var cls = CoreCache.getInstance().getClass("com.simplicite.commons.AIBySimplicite.AITools");
	// Constructeur vide
	var obj = cls.newInstance();
	// Methode à appeler avec les Class des arguments
	var method = cls.getMethod("actionAICaller", g.getClass() ,java.lang.String,java.lang.String,com.simplicite.util.ObjectDB/*, ... */);
	// Invoke dynamique avec les paramètres
	var result = method.invoke(obj, g,spe,prompt,object /*, ... */);
	if(result.has("error")){
		return Message.formatError("AI_ERROR_RETURN",result.getString("code")+": "+result.getString("error"),"demoPrdDescription");

	}

	if(!result.has("choices")){
		return Message.formatWarning(null, "Unexpected response", null);
	}
	return Message.formatSimpleText(result.getJSONArray("choices").getJSONObject(0).getJSONObject("message").getString("content"));

}
(function(obj){
	var act=  obj.getAction(obj.getCurrentAction());
	var fld =obj.getConfirmField(act, "AIActPrompt");
	var spe = "";
	var fldObj = Grant.getSystemAdmin().getTmpObject("Field");
	fldObj.select(fld.getId());
	spe= fldObj.getFieldValue("AIFldSpe");

	return callAITools(fld.getValue(),spe,obj);
})([OBJECT]);
